  $: {
    const w = 0 | (innerWidth / 6)
    const h = 0 | (innerHeight / 16)
    out = [...new Array(h)].map(
      (_, i) =>
        [...new Array(w)].map((_, j) =>
          i == 0 && j == 1 ? '╔'
          : i == 0 && j == w - 2 ? '╗'
          : i == h - 1 && j == 1 ? '╚'
          : i == h - 1 && j == w - 2 ? '╝'
          : [0, w - 1].includes(j) ? ' '
          : [0, h - 1].includes(i) ? '═'
          : [1, w - 2].includes(j) ? '║'
          : [2, w - 3].includes(j) ? ' '
          : randitem(chars)
